mapper.update:
UPDATE banners
    SET title = #{title}
        ,image_url = #{imageUrl}
        ,link_url = #{linkUrl}
        ,description = #{description}
        ,start_date = #{startDate}
        ,end_date = #{endDate}
        ,seq = #{seq}
        ,is_active = #{isActive}
        ,position = #{position}
WHERE no = #{no}
