mapper.update:
UPDATE users
SET username = #{username},
    age = #{age},
    gender = #{gender},
    address = #{address}
WHERE no = #{no}

UPDATE users
SET password = #{password}
WHERE no = #{no}

UPDATE users
SET profile_img = #{profileImg}
WHERE no = #{no}

mapper.select:
SELECT COUNT(*)
FROM users
WHERE user_id = #{userId}

SELECT COUNT(*)
FROM users
WHERE username = #{username}

mapper.update:
UPDATE users
SET report_count = IFNULL(report_count, 0) + 1
WHERE no = #{targetNo}

mapper.select:
SELECT IFNULL(report_count, 0)
FROM users
WHERE no = #{targetNo}

mapper.insert:
INSERT INTO user_bans (
    user_no, reason, report_count_at_ban, ban_type, ban_end_date, is_active, created_at, updated_at
) VALUES (
    #{userNo}, #{reason}, #{reportCountAtBan}, #{banType}, #{banEndDate}, #{isActive}, NOW(), NOW()
